import React, { useState } from 'react';
import './Pizza.css';

function pizzaApp() {
  return (
    <div className="App">
      test
    </div>
  );
}

class Toppings extends React.Component {
  constructor(props) {
    super(props);
    this.active = false;
  }

  toggle() {
    this.active = !(this.active);
  }
}

const Crust = Object.freeze({
  Thick: Symbol("Thick"),
  Thin: Symbol("Thin")
});

const Sauce = Object.freeze({
  Red: Symbol("Red"),
  White: Symbol("White"),
  None: Symbol("No sauce")
});

class Pizza extends React.Component {
  constructor(props) {
    super(props);
    this.toppings = new Set();
    this.crust = Crust.Thick;
    this.sauce = Sauce.Red;
  }

  get getTops() {
    return this.toppings;
  }

  get getNumTops() {
    return this.toppings.size;
  }

  // Returns 'true' if topping was successfully added to set
  // 'add()' normally returns Set object itself
  addTopping(topping) {
    let i = this.toppings.size;
    this.toppings.add(topping);
    return this.toppings.size === i + 1;
  }

  // Returns 'true' if topping was successfully removed from set
  removeTopping(topping) {
    let i = this.toppings.size;
    this.toppings.delete(topping);
    return this.toppings.size === i - 1;
  }

  clearToppings() {
    this.toppings.clear();
  }

  equals(other) {
    return this.toppings.size === other.getNumTops() &&
      this.toppings.every((x) => other.getTops().has(x));
  }

  render() {
    return (
      <div>
        Pizza app coming soon
      </div>
    );
  }
}

class Title extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {
    return (
      <div className='Title'>
        <h1>{this.props.title}</h1>
      </div>
    )
  }
}

class PizzaButton extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
      <div className='Center'>
        <button
          className='Button'
          onClick={() => this.props.onClick()}>
          ADD A PIZZA
        </button>
      </div>
    );
  }
}

class NewPizza extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      Mushrooms: false,
      Pepperony: false,
      BellPeppers: false,
      Artichoke: false,
      Olives: false
    }
  }

  render() {
    return (
      <div>
        <div
          className='Center'>
          CHOOSE WHICH TOPPINGS YOU'D LIKE TO ADD TO YOUR PIZZA
        </div>
        <div className='Center'>
          <button
            className='Button'
            style={{ background: this.state.Mushrooms ? '#999' : '#fff' }}
            onClick={() => this.setState({ Mushrooms: !this.state.Mushrooms })}>
            MUSHROOMS
          </button>
          <button
            className='Button'
            style={{ background: this.state.Pepperony ? '#999' : '#fff' }}
            onClick={() => this.setState({ Pepperony: !this.state.Pepperony })}>
            PEPPERONY
          </button>
          <button
            className='Button'
            style={{ background: this.state.BellPeppers ? '#999' : '#fff' }}
            onClick={() => this.setState({ BellPeppers: !this.state.BellPeppers })}>
            BELL PEPPERS
          </button>
          <button
            className='Button'
            style={{ background: this.state.Artichoke ? '#999' : '#fff' }}
            onClick={() => this.setState({ Artichoke: !this.state.Artichoke })}>
            ARTICHOKE
          </button>
          <button
            className='Button'
            style={{ background: this.state.Olives ? '#999' : '#fff' }}
            onClick={() => this.setState({ Olives: !this.state.Olives })}>
            OLIVES
          </button>
        </div>
        <div className='Center'>
          <button
            className='Button'
            onClick={() => this.props.onComplete(JSON.stringify(this.state))}>
            SUBMIT
          </button>
        </div>
      </div>)
  }
}

class PizzaApp extends React.Component {
  constructor(props) {
    super(props);
    this.title = "RICARDO'S PIZZA SHOP";
    this.state = {
      creatingPizza: false,
      duplicatePizza: false,
      pizzas: null
    }
  }

  addPizzaClick() {
    this.setState({ creatingPizza: true });
  }

  newPizzaAdded(newPizza) {
    let pizzaSet = this.state.pizzas ?? new Set();
    let preSize = pizzaSet.size;
    pizzaSet.add(newPizza);
    let doop = preSize == pizzaSet.size; // If pizza was not added to set, set size won't change
    this.setState({
      creatingPizza: false,
      duplicatePizza: doop,
      pizzas: pizzaSet,
    });
  }

  renderPromptSection() {
    if (this.state.creatingPizza) {
      return (
        <NewPizza onComplete={(newPizza) => this.newPizza = this.newPizzaAdded(newPizza)} />
      )
    }
    else return (
      <PizzaButton onClick={() => this.addPizzaClick()} />
    )
  }

  renderPizzas() {
    if (this.state.pizzas) {
      return(<div className='Center'>
        IF PIZZA EXISTS
      </div>)
    } else return null
  }

  render() {
    return (
      <div>
        <Title title={this.title} />
        {this.renderPromptSection()}
        {this.renderPizzas()}
      </div>)
  }
}

class App extends React.Component {
  render() {
    return (
      <PizzaApp />
    );
  }
}


export default App
