import React from 'react';
import './App.css';

function pizzaApp() {
  return (
    <div className="App">
      test
    </div>
  );
}

class Toppings extends React.Component {
  constructor(props) {
    super(props);
    this.active = false;
  }

  toggle() {
    this.active = !(this.active);
  }
}

const Crust = Object.freeze({
  Thick: Symbol("Thick"),
  Thin: Symbol("Thin")
});

const Sauce = Object.freeze({
  Red: Symbol("Red"),
  White: Symbol("White"),
  None: Symbol("No sauce")
});

class Pizza extends React.Component {
  constructor(props) {
    super(props);
    this.toppings = new Set();
    this.crust = Crust.Thick;
    this.sauce = Sauce.Red;
  }

  get getTops() {
    return this.toppings;
  }

  get getNumTops() {
    return this.toppings.size;
  }

  // Returns 'true' if topping was successfully added to set
  // 'add()' normally returns Set object itself
  addTopping(topping) {
    let i = this.toppings.size;
    this.toppings.add(topping);
    return this.toppings.size === i + 1;
  }

  // Returns 'true' if topping was successfully removed from set
  removeTopping(topping) {
    let i = this.toppings.size;
    this.toppings.delete(topping);
    return this.toppings.size === i - 1;
  }

  clearToppings() {
    this.toppings.clear();
  }

  equals(other) {
    return this.toppings.size === other.getNumTops() &&
      this.toppings.every((x) => other.getTops().has(x));
  }

  render() {
    return (
      <div>
        Pizza app coming soon
      </div>
    );
  }
}

class PizzaList extends React.Component {
  constructor(props) {
    super(props);
    this.pizzas = new Set();
  }



}

class Header extends React.Component {
  render() {
    return (
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
        <h1> RICARDO'S PIZZA SHOP </h1>
      </div>
    )
  }
}

class App extends React.Component {
  render() {
    return (
      <div className="App">
        <Header />
      </div>
    );
  }
}


export default App
