import React, { useState } from 'react';
import './Pizza.css';

function pizzaApp() {
  return (
    <div className="App">
      test
    </div>
  );
}

class Toppings extends React.Component {
  constructor(props) {
    super(props);
    this.active = false;
  }

  toggle() {
    this.active = !(this.active);
  }
}

const Crust = Object.freeze({
  Thick: Symbol("Thick"),
  Thin: Symbol("Thin")
});

const Sauce = Object.freeze({
  Red: Symbol("Red"),
  White: Symbol("White"),
  None: Symbol("No sauce")
});

class Pizza extends React.Component {
  constructor(props) {
    super(props);
    this.toppings = new Set();
    this.crust = Crust.Thick;
    this.sauce = Sauce.Red;
  }

  get getTops() {
    return this.toppings;
  }

  get getNumTops() {
    return this.toppings.size;
  }

  // Returns 'true' if topping was successfully added to set
  // 'add()' normally returns Set object itself
  addTopping(topping) {
    let i = this.toppings.size;
    this.toppings.add(topping);
    return this.toppings.size === i + 1;
  }

  // Returns 'true' if topping was successfully removed from set
  removeTopping(topping) {
    let i = this.toppings.size;
    this.toppings.delete(topping);
    return this.toppings.size === i - 1;
  }

  clearToppings() {
    this.toppings.clear();
  }

  equals(other) {
    return this.toppings.size === other.getNumTops() &&
      this.toppings.every((x) => other.getTops().has(x));
  }

  render() {
    return (
      <div>
        Pizza app coming soon
      </div>
    );
  }
}

class Title extends React.Component {
  constructor(props) {
    super(props);
  }
  render() {
    return (
      <div className='Title'>
        <h1>{this.props.title}</h1>
      </div>
    )
  }
}

class PizzaButton extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
      <div className='Center'>
        <button
          className='Button'
          onClick={() => this.props.onClick()}>
          ADD A PIZZA
        </button>
      </div>
    );
  }
}

class NewPizza extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      Mushrooms: false,
      Pepperony: false,
      BellPeppers: false,
      Artichoke: false,
      Olives: false
    }
  }

  render() {
    return (
      <div className='Center'>
        <div
          className='Center'>
          CHOOSE WHICH TOPPINGS YOU'D LIKE TO ADD TO YOUR PIZZ
        </div>
        <button
          className='Button'
          onClick={() => this.setState({ Mushrooms: !this.state.Mushrooms })}>
          MUSHROOMS
        </button>
      </div>)
  }
}

class PizzaApp extends React.Component {
  constructor(props) {
    super(props);
    this.title = "RICARDO'S PIZZA SHOP";
    this.newPizza = null;
    this.state = {
      pizzas: new Set(),
      creatingPizza: false
    }
  }

  addPizzaClick() {
    this.setState({ creatingPizza: true });
  }

  newPizzaAdded() {

  }

  renderPromptSection() {
    if (this.state.creatingPizza) {
      return (
        <NewPizza onComplete={() => this.newPizzaAdded()} />
      )
    }
    else return (
      <PizzaButton onClick={() => this.addPizzaClick()} />
    )
  }

  render() {
    return (
      <div>
        <Title title={this.title} />
        {this.renderPromptSection()}
        {this.state.temp}
      </div>)
  }
}

class App extends React.Component {
  render() {
    return (
      <PizzaApp />
    );
  }
}


export default App
